import React, { useRef, useState, useEffect } from "react";
import "./Css/VideoPlayer.css";

const formatTime = (seconds) => {
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs < 10 ? "0" : ""}${secs}`;
};

const VideoPlayer2 = () => {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [quality, setQuality] = useState("320");
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isMuted, setIsMuted] = useState(false);
  const [lastVolume, setLastVolume] = useState(1);
  const [prevQuality, setPrevQuality] = useState("320");
const [isTransitioning, setIsTransitioning] = useState(false);


  // Play / Pause toggle
  const togglePlay = () => {
    const video = videoRef.current;
    if (!video) return;
    if (video.paused) {
      video.play();
    } else {
      video.pause();
    }
  };

  // Volume change
  const handleVolumeChange = (e) => {
    const vol = parseFloat(e.target.value);
    setVolume(vol);
    videoRef.current.volume = vol;
    setIsMuted(vol === 0);
  };

  // Mute toggle
  const toggleMute = () => {
    const video = videoRef.current;

  if (video.muted || volume === 0) {
    // Unmute
    setVolume(lastVolume);
    video.volume = lastVolume;
    video.muted = false;
    setIsMuted(false);
  } else {
    // Mute
    setLastVolume(volume);
    setVolume(0);
    video.volume = 0;
    video.muted = true;
    setIsMuted(true);
  }
  };

  const handleQualityChange = async(e) => {
    const newQuality = e.target.value;
    const video = videoRef.current;
    const wasPaused = video.paused;
    const current = video.currentTime;

    const goingLower = parseInt(newQuality) < parseInt(prevQuality);
  setIsTransitioning(true);
  setPrevQuality(newQuality);
  setQuality(newQuality);
  setIsPlaying(false);

  


    setQuality(newQuality);
     setIsPlaying(false);
    video.src = `/videos/SampleVideo-${newQuality}p.mp4`;
    video.load();
    video.currentTime = current;
   try {
    if (!wasPaused) {
      await video.play();
      setIsPlaying(true)
    }
  } catch (error) {
    console.error("Autoplay failed after quality switch:", error);
  }

  setTimeout(() => {
    video.classList.remove("blurred"); 
    setIsTransitioning(false);
  }, 1000);
  };

  const handleSeek = (e) => {
    const time = parseFloat(e.target.value);
    videoRef.current.currentTime = time;
    setCurrentTime(time);
  };

  const handleFullscreen = () => {
    const video = videoRef.current;
    if (video.requestFullscreen) {
      video.requestFullscreen();
    } else if (video.webkitRequestFullscreen) {
      video.webkitRequestFullscreen();
    } else if (video.msRequestFullscreen) {
      video.msRequestFullscreen();
    }
  };

  useEffect(() => {
    const video = videoRef.current;

    const updateTime = () => {
      setCurrentTime(video.currentTime);
    };

    const setVideoData = () => {
      setDuration(video.duration);
      setCurrentTime(video.currentTime);
    };

    const onPlay = () => setIsPlaying(true);
    const onPause = () => setIsPlaying(false);

    video.addEventListener("loadedmetadata", setVideoData);
    video.addEventListener("timeupdate", updateTime);
    video.addEventListener("play", onPlay);
    video.addEventListener("pause", onPause);

    return () => {
      video.removeEventListener("loadedmetadata", setVideoData);
      video.removeEventListener("timeupdate", updateTime);
      video.removeEventListener("play", onPlay);
      video.removeEventListener("pause", onPause);
    };
  }, []);

  useEffect(() => {
  const seekBar = document.querySelector('.seek-bar-container input[type="range"]');
  const percentage = (currentTime / duration) * 100;
  if (seekBar) {
    seekBar.style.setProperty('--seek-before-width', `${percentage}%`);
  }
}, [currentTime, duration]);


useEffect(() => {
  const volumeSlider = document.querySelector('.volume-slider');
  if (volumeSlider) {
    const percent = volume * 100;
    volumeSlider.style.background = `linear-gradient(to right, #2196F3 ${percent}%, #ccc ${percent}%)`;
  }
}, [volume]);

  return (
  <div className="yt-player-container">
    <video
      ref={videoRef}
      className={`yt-video ${isTransitioning ? "blurred" : ""}`}
      src={`/videos/SampleVideo-${quality}p.mp4`}
      onClick={togglePlay}
    ></video>

    <div className="yt-controls">
      {/* Seek Bar - full width */}
      <div className="seek-bar-container">
        <input
          type="range"
          min="0"
          max={duration}
          step="0.1"
          value={currentTime}
          onChange={handleSeek}
        />
      </div>

      {/* Control Row */}
      <div className="control-row">
        <button onClick={togglePlay}>{isPlaying ? "‚ùö‚ùö" : "‚ñ∂"}</button>

        <span className="time-display">
          {formatTime(currentTime)} / {formatTime(duration)}
        </span>

        <button onClick={toggleMute}>{isMuted ? "üîá" : "üîä"}</button>

        <input
          type="range"
          className="volume-slider"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
        />

        <select value={quality} onChange={handleQualityChange}>
          <option value="320">320p</option>
          <option value="480">480p</option>
          <option value="720">720p</option>
          <option value="1080">1080p</option>
        </select>

        <button onClick={handleFullscreen}>‚õ∂</button>
      </div>
    </div>
  </div>


  );
};

export default VideoPlayer2;
