import React, { useRef, useState, useEffect } from "react";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const [quality, setQuality] = useState("720");

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const currentTime = video.currentTime;
    const isPaused = video.paused;

    video.src = `/videos/sample-${quality}p.mp4`;
    video.load();

    const handleLoadedData = () => {
      video.currentTime = currentTime;
      if (!isPaused) {
        video.play();
      }
    };

    video.addEventListener("loadeddata", handleLoadedData);

    return () => {
      video.removeEventListener("loadeddata", handleLoadedData);
    };
  }, [quality]);

  const handleQualityChange = (e) => {
    setQuality(e.target.value);
  };

  return (
    <div className="video-container">
      <video ref={videoRef} controls width="640" height="360">
        <source src={`/videos/sample-${quality}p.mp4`} type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      <div className="quality-selector">
        <label htmlFor="qualitySelect">Select Quality:</label>
        <select
          id="qualitySelect"
          value={quality}
          onChange={handleQualityChange}
        >
          <option value="320">320p</option>
          <option value="480">480p</option>
          <option value="720">720p</option>
          <option value="1080">1080p</option>
        </select>
      </div>
    </div>
  );
};

export default VideoPlayer;
